 sync_cpllpd : gol_quad_sync_block
  port map
         (
            clk             =>  gtrefclk0_in,
            data_in         =>  cpllpd_in,
            data_out        =>  cpllpd_sync
         );
 cpll_pd_i <= cpllpd_sync or cpllpd_ovrd_i;

process (drpclk_in)
begin
if(drpclk_in'event and drpclk_in = '1') then 
  if(cpllreset_in = '1' and ack_flag = '0') then
    flag <= not flag;
    flag2 <= '1';
  else
    flag <= flag; 
    flag2 <= '0';
end if;
end if;
end process;


process (drpclk_in)
begin
if(drpclk_in'event and drpclk_in = '1') then 
  if(flag2 = '1') then
   ack_flag <= '1';
 elsif(ack_i = '1') then
   ack_flag <= '0';
 end if;
end if;
end process;


