  function get_cdrlock_time(is_sim : in integer) return integer is
    variable lock_time: integer;
  begin
    if (is_sim = 1) then
      lock_time := 1000;
    else
      lock_time := 50000 / integer(1.6); --Typical CDR lock time is 50,000UI as per DS183
    end if;
    return lock_time;
  end function;


